                <svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" id="body_2" preserveAspectRatio="xMinYMin meet" viewBox="0 0 793 1121"><defs><clipPath id="1"><path id="" clip-rule="evenodd" d="M0 841L0 -0.9000244L0 -0.9000244L595.3 -0.9000244L595.3 -0.9000244L595.3 841L595.3 841L0 841z" /></clipPath></defs><g transform="matrix(1.333333 0 0 1.333333 0 0)"><g clip-path="url(#1)"><g transform="matrix(0.4074468 0 0 0.4074856 161.444 1.039063)"><g transform="matrix(1 0 0 1 0 0)"><image x="0" y="0" xlink:href="img_08.png" width="693" height="417" /></g></g><g transform="matrix(0.3274944 0 0 0.3268726 175.066 246.1871)"><g transform="matrix(1 0 0 1 0 0)"><image x="0" y="0" xlink:href="img_09.png" width="779" height="174" /></g></g><g transform="matrix(0.3273098 0 0 0.3272119 166.112 314.9901)"><g transform="matrix(1 0 0 1 0 0)"><image x="0" y="0" xlink:href="img_10.png" width="834" height="438" /></g></g><g transform="matrix(0.3885878 0 0 0.3883342 167.979 507.915)"><g transform="matrix(1 0 0 1 0 0)"><image x="0" y="0" xlink:href="img_11.png" width="693" height="534" /></g></g><g transform="matrix(0.3274944 0 0 0.3279273 175.066 779.584)"><g transform="matrix(1 0 0 1 0 0)"><image x="0" y="0" xlink:href="img_12.png" width="779" height="173" /></g></g></g></g></svg>
