="M147.227 263.4052L445.9731 263.4052" stroke="#FFD900" stroke-width="0.5" fill="none" stroke-opacity="0.1372549" /><g transform="matrix(0.3275975 0 0 0.3276242 148.034 636.907)"><g transform="matrix(1 0 0 1 0 0)"><image x="0" y="0" xlink:href="img_03.png" width="907" height="549" /></g></g><path id="340" transform="matrix(1 0 0 -1 0 841)" d="M147.784 204.093L147.784 24.22729" stroke="#FFD900" stroke-width="0.5" fill="none" stroke-opacity="0.1372549" /><path id="341" transform="matrix(1 0 0 -1 0 841)" d="M445.4149 204.093L445.4149 24.22729" stroke="#FFD900" stroke-width="0.5" fill="none" stroke-opacity="0.1372549" /><path id="342" transform="matrix(1 0 0 -1 0 841)" d="M147.534 204.343L445.6649 204.343" stroke="#FFD900" stroke-width="0.5" fill="none" stroke-opacity="0.1372549" /><path id="343" transform="matrix(1 0 0 -1 0 841)" d="M147.534 23.97729L445.6649 23.97729" stroke="#FFD900" stroke-width="0.5" fill="none" stroke-opacity="0.1372549" /></g></g></svg>
